import React, {useState, useEffect} from 'react'
import {Link,Router} from 'react-router-dom'
import PokemonBattles from './PokemonBattles'
import axios from 'axios'


export default function PokemonCatch() {

  const [pokemon, setPokemon]=useState([])
  const [starter, setStarter]=useState([])
  const [yourPokemon, setYourPokemon]=useState([])
  const [toggle, setToggle]=useState(true)
  console.log(pokemon)
  console.log(starter)
  console.log(toggle)
  console.log(111,yourPokemon)

    function getFirstThree(){
      setStarter(pokemon.slice(0,3))
      setToggle(toggle!==toggle)
    }


useEffect(()=>{
axios.get('https://api.pokemontcg.io/v1/cards')
.then(res=>{
 const filtered=res.data.cards.filter(one=>one.supertype!=="Energy"&&one.supertype!=="Trainer"&&one.attacks.every(attack=> attack.damage))
  setPokemon(filtered)
})
},[])

function movedToArray(...id){
  setYourPokemon(id)
  }
  const starters=starter.map(function(choose){
  return <div>  
    <p>
      Name: {choose?.name}</p>
  <img src={choose?.imageUrl}/>
<p>hp:{choose?.hp}</p>
<button onClick={()=>movedToArray(
  choose.id,choose.attacks,choose.name,choose.supertype,choose.types,
  choose.weaknesses,choose.imageUrl,choose.hp
  )} >
    i choose {choose.name}
</button>
</div>
  })

  return (
    <div>
     { toggle? 
     <>
     <h2 className='intro'>welcome trainer to a world filled with brutality where you capture innocent creatures and force them to fight to the death, or capture and force into servitude... your choice!</h2>
    <button onClick={getFirstThree} className='choose'>choose your starter servant... i mean pokemon</button>
    </>
    :
     <div>
  {starters}
     </div>
  }
  </div>
  )
}
