import React, {useState,useEffect} from 'react'
import {useParams} from 'react-router-dom'
import axios from 'axios'


export default function PokemonBattles(props) {
  const {pokeId} = useParams()
  const [yourPokemon, setYourPokemon]=useState([])
  console.log(yourPokemon)

  useEffect(()=> {
    axios.get('https://api.pokemontcg.io/v1/cards/' + pokeId).then(res => {
      setYourPokemon(res.data.card)
    })
  },[])


useEffect(()=>{
  axios.get('https://api.pokemontcg.io/v1/cards')
  .then(res=>{
   const filtered=res.data.cards.filter(one=>one.supertype!=="Energy"&&one.supertype!=="Trainer"&&one.attacks.every(attack=> attack.damage))
    setPokemon(filtered)
  })
  },[])

  return (
    <div>
      <h3>congradulations trainer you have chosen {yourPokemon.name}, go forth and bring chaos and destrucion upon your enemies and enslave... caputure as many monsters as you can</h3>
    </div>
  )
}
