import React, {useState,useEffect,useCallback} from 'react'
import {useParams} from 'react-router-dom'
import fire from './images/fire.jpeg'
import water from './images/water.jpeg'
import fight from './images/fight.jpg'
import forest from './images/forest.jpg'
import axios from 'axios'


export default function PokemonBattles(props) {
  const {pokeId} = useParams()
  const [pokemon, setPokemon]=useState([])
  const [yourPokemon, setYourPokemon]=useState([])
  const [toggle, setToggle]=useState(true)
  const [images, setImages]=useState([fire,water,fight,forest])
  const [randomImage, setRandomImage]=useState([fire])
  const [secondPokemon, setSecondPokemon]=useState({})
  const [canCatch, setCanCatch]=useState(true)

function toggled(){
  setToggle(toggle!==toggle)
}

  useEffect(()=> {
    axios.get('https://api.pokemontcg.io/v1/cards/' + pokeId).then(res => {
      setYourPokemon([res.data.card])
    })
  },[])


  useEffect(()=>{
    axios.get('https://api.pokemontcg.io/v1/cards')
    .then(res=>{
      const filtered=res.data.cards.filter(one=>one.supertype!=="Energy"&&one.supertype!=="Trainer"&&one.attacks.every(attack=> attack.damage))
      setPokemon(filtered)
      setSecondPokemon(filtered[Math.floor(Math.random()*filtered.length)])
  })
  },[])

  function getRandom(){
    setRandomImage(images[Math.floor(Math.random()*images.length)])
  }


  function fights(){
    setSecondPokemon(prev => {
      const firstRandom=Math.floor(Math.random()*secondPokemon.attacks.length)
      const hp = parseInt(prev.hp) - parseInt(yourPokemon[0].attacks[firstRandom].damage)
       if(hp<=0){
        return pokemon[Math.floor(Math.random()*pokemon.length)]
        }
        return {
          ...prev,
          hp
        }
      })
      setYourPokemon(prev=>{
        const secondRandom=Math.floor(Math.random()*yourPokemon.attacks.length)
        console.log(secondRandom)
        const hp = parseInt(prev.hp) - parseInt(secondPokemon.attacks[secondRandom].damage)
        if(hp<=0){
          return yourPokemon[Math.floor(Math.random()*pokemon.length)]
        }
        return {
          ...prev,
          hp
        }
      })
    }

    console.log(222222,yourPokemon)
  console.log(111111,secondPokemon)


  function capture(...second){
    if(parseInt(second[0].hp)<=120){
      setYourPokemon([yourPokemon, second[0]])
    }
    if(canCatch===false){
      alert('you can only capture a pokemon if its hp is 40 or less')
    }
  }


  return (
    <div>
      { toggle?
        <div>
          <h3>congradulations trainer you have chosen {yourPokemon.name}, go forth and bring chaos and destrucion upon your enemies and enslave... caputure as many monsters as you can</h3>
          <button onClick={toggled}>continue on your adventure</button>
        </div>

        :

        <div>
          <img src={randomImage} className='randomImage'/>
          <button onClick={getRandom} className='button'> walk</button>
          <button onClick={fights} className='button1'>fight</button>
          <button onClick={()=>capture(secondPokemon)} className='pokeball'>throw Pokeball</button>

          <div className='poke1'>
          <p className='name'>{yourPokemon?.name}</p>
          <img src={yourPokemon?.imageUrl}/>
          <p className='hp'>HP:{yourPokemon?.hp}</p>
         </div>

         <div className='poke2'>
          <p className='name'>{secondPokemon?.name}</p>
          <img src={secondPokemon?.imageUrl}/>
          <p className='hp'>HP:{secondPokemon?.hp}</p>
         </div>

        </div>

      }
    </div>
  )
}
